	const KD_CLASS_TO_TYPE = {onyomi: "reading", translation: "kanji", component: "radical"};
//	const HTML_TAG_PARSER = /<([^\/> ]+)(?:[^>]*(?:class|href|src)="([^>]*)")?[^>]*>(?:(                                                     [^]*?)<\/\1>)?/; // assumption: nested tags are always of different types, e.g. NOT: <p><p>...</p></p>
